mapper.select:
SELECT * 
  FROM 
       (SELECT count(*) AS brr 
         FROM HL_SERVICE 
        WHERE service_cd='2' 
              AND user_no=#{userNo} 
              AND sysdate BETWEEN service_start AND service_end
       ) brr, 
       (SELECT count(*) AS rev 
         FROM HL_SERVICE 
        WHERE service_cd='2' 
              AND user_no=#{userNo} 
              AND service_start > sysdate
       ) rev, 
       (SELECT count(*) AS arr 
         FROM HL_SERVICE 
        WHERE service_cd='2' 
              AND user_no=#{userNo} 
              AND service_end < sysdate
       ) arr, 
       (SELECT count(*) AS san 
         FROM HL_USER_SAN 
        WHERE user_no=#{userNo}
       ) san  
 WHERE 1=1

SELECT BK.BOOK_ISBN, BK.BOOK_NO, BK.BOOK_NM, TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD(DY)') AS BOOK_DATE, TO_CHAR(SV.SERVICE_END,'YYYY-MM-DD(DY)') AS BOOK_RETURN, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE) AS DIV_CD, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE) AS CLASS_CD, BK.BOOK_PUB, BK.BOOK_WRITER
FROM OFFEDU_TESTDB.HL_BOOK BK, OFFEDU_TESTDB.HL_USER US, OFFEDU_TESTDB.HL_SERVICE SV
WHERE 1=1 AND BK.BOOK_NO = SV.BOOK_NO AND US.USER_NO=SV.USER_NO and US.USER_NO=#{userNo} and sysdate between SV.SERVICE_START and service_end
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') >= #{brrStr}
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') <= #{brrEnd}
and TO_CHAR(SV.SERVICE_END + 15,'YYYYMMDD') >= #{retStr}
and TO_CHAR(SV.SERVICE_END + 15,'YYYYMMDD') <= #{retEnd}
and upper(book_writer) like upper('%'||#{writer}||'%')
and upper(book_pub) like upper('%'||#{pub}||'%')
and BK.CLASS_CD = #{clCd}
and BK.DIV_CD = #{divCd}
and ( upper(BK.BOOK_ISBN) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_NM) like upper('%'||#{keyword}||'%') or upper(TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD(DY)')) like upper('%'||#{keyword}||'%') or upper(TO_CHAR(SV.SERVICE_END,'YYYY-MM-DD(DY)')) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_PUB) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_WRITER) like upper('%'||#{keyword}||'%')
)

SELECT BK.BOOK_ISBN, BK.BOOK_NM, TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD(DY)') AS BOOK_DATE, TO_CHAR(SV.SERVICE_END,'YYYY-MM-DD(DY)') AS BOOK_RETURN, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE) AS DIV_CD, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE) AS CLASS_CD, BK.BOOK_PUB, BK.BOOK_WRITER
FROM OFFEDU_TESTDB.HL_BOOK BK, OFFEDU_TESTDB.HL_USER US, OFFEDU_TESTDB.HL_SERVICE SV
WHERE 1=1 AND BK.BOOK_NO = SV.BOOK_NO AND US.USER_NO=SV.USER_NO and US.USER_NO=#{userNo} and SV.SERVICE_END < sysdate
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') >= #{brrStr}
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') <= #{brrEnd}
and TO_CHAR(SV.SERVICE_END,'YYYYMMDD') >= #{retStr}
and TO_CHAR(SV.SERVICE_END,'YYYYMMDD') <= #{retEnd}
and upper(book_writer) like upper('%'||#{writer}||'%')
and upper(book_pub) like upper('%'||#{pub}||'%')
and BK.CLASS_CD = #{clCd}
and BK.DIV_CD = #{divCd}
and ( upper(BK.BOOK_ISBN) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_NM) like upper('%'||#{keyword}||'%') or upper(TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD(DY)')) like upper('%'||#{keyword}||'%') or upper(TO_CHAR(SV.SERVICE_END,'YYYY-MM-DD(DY)')) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_PUB) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_WRITER) like upper('%'||#{keyword}||'%')
)

SELECT user_no, book_no, book_isbn, book_nm, div_cd, class_cd, book_rev_brr, book_pub, book_writer, rank FROM (SELECT sv.USER_NO AS USER_NO, sv.SERVICE_NO as SERVICE_NO, BK.BOOK_NO as BOOK_NO, BK.BOOK_ISBN AS BOOK_ISBN, BK.BOOK_NM AS BOOK_NM, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE ) AS DIV_CD, (SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE ) AS CLASS_CD, TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD(DY)') AS BOOK_REV_BRR , BK.BOOK_PUB AS BOOK_PUB, BK.BOOK_WRITER AS BOOK_WRITER, ROW_NUMBER() OVER (ORDER BY sv.SERVICE_START ASC) || '순위' AS RANK FROM HL_BOOK BK, HL_USER US, HL_SERVICE SV WHERE 1=1 AND BK.BOOK_NO = SV.BOOK_NO AND US.USER_NO=SV.USER_NO AND SV.SERVICE_CD='2' AND SV.SERVICE_START > sysdate
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') >= #{brrStr}
and TO_CHAR(SV.SERVICE_START,'YYYYMMDD') <= #{brrEnd}
and BK.CLASS_CD = #{clCd}
and BK.DIV_CD = #{divCd}
and upper(book_writer) like upper('%'||#{writer}||'%')
and upper(book_pub) like upper('%'||#{pub}||'%')
and ( upper(BK.BOOK_ISBN) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_NM) like upper('%'||#{keyword}||'%') or upper(TO_CHAR(BK.BOOK_DATE+16,'YYYY-MM-DD(DY)')) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE)) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_PUB) like upper('%'||#{keyword}||'%') or upper(BK.BOOK_WRITER) like upper('%'||#{keyword}||'%')
)
) a WHERE USER_NO=#{userNo}

SELECT rnum, x.san_name AS SANNAME, to_char(x.san_date,'yyyy-mm-dd(dy)') AS SANDATE FROM (SELECT ROWNUM AS RNUM , a.USER_NO AS USER_NO , b.SAN_NAME AS SAN_NAME , c.SAN_DATE AS SAN_DATE, b.SAN_CD AS SAN_CD FROM HL_USER a, HL_SAN_CD b, HL_USER_SAN c WHERE a.USER_NO=c.USER_NO AND c.SAN_CD=b.SAN_CD ) x WHERE 1=1 AND x.user_no = #{userNo}
and TO_CHAR(x.san_date,'YYYYMMDD') >= #{sanStr}
and TO_CHAR(x.san_date,'YYYYMMDD') <= #{sanEnd}
and x.san_cd = #{sanCd}
and ( upper(x.san_name) like upper('%'||#{keyword}||'%') or upper(x.san_date ) like upper('%'||#{keyword}||'%') )
ORDER BY rnum

SELECT BK.BOOK_ISBN, BK.BOOK_NO, BK.BOOK_NM, TO_CHAR(SV.SERVICE_START,'YYYY-MM-DD') AS BOOK_DATE, TO_CHAR(SV.SERVICE_END,'YYYY-MM-DD') AS BOOK_RETURN, nvl((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A02' AND BK.DIV_CD = DTL.DETAIL_CODE),'-') AS DIV_CD, nvl((SELECT DETAIL_NAME FROM HL_DTL_CD DTL WHERE COMM_CODE='A01' AND BK.CLASS_CD = DTL.DETAIL_CODE),'-') AS CLASS_CD, BK.BOOK_PUB, BK.BOOK_WRITER, BK.BOOK_IDX, Bk.BOOK_SUM
FROM OFFEDU_TESTDB.HL_BOOK BK, OFFEDU_TESTDB.HL_SERVICE SV
WHERE 1=1 AND BK.BOOK_NO = SV.BOOK_NO AND BK.BOOK_NO = #{bookNo}

mapper.update:
UPDATE OFFEDU_TESTDB.HL_SERVICE
		SET    
			SERVICE_END = #{bookReturn}
		WHERE  BOOK_NO        = #{bookNo}
